mapper.select:
SELECT *
FROM member
WHERE user_id = #{user_id}
AND password = #{password}

mapper.update:
UPDATE member
SET
	password = #{password},
	nickname = #{nickname},
	profile_img = #{profile_img}
WHERE user_id = #{user_id}

UPDATE member
SET fail_num = #{fail_num}
WHERE user_id = #{user_id}

UPDATE member
SET lock_yn = #{lock_yn}
WHERE user_id = #{user_id}

mapper.select:
SELECT (CASE WHEN count(user_id) = 1 THEN user_id ELSE 'N' END) as user_id
FROM member
               WHERE 1=1
					AND name = #{name}
					AND email = #{email}
			   GROUP BY user_id

SELECT count(user_id) FROM member
WHERE user_id = #{user_id}
  AND name = #{name}
  AND email = #{email}

mapper.update:
UPDATE member
SET password = #{password}
  	, lock_yn = #{lock_yn}
	, fail_num = #{fail_num}
WHERE user_id = #{user_id}
  AND name = #{name}
  AND email = #{email}
