mapper.insert:
INSERT INTO studylogs
(
    post_id
    , user_id
    , group_id
    , title
    , content
    , create_date

)
VALUES (
    ( SELECT COALESCE(CAST(max(post_id) AS INTEGER) + 1 , 1)
               FROM studylogs WHERE group_id = #{group_id}
               )
    , #{user_id}
    , #{group_id}
    , #{title}
    , #{content}
    , now()
)
